import React from "react";
import { StyleSheet, Text, View } from "react-native";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { shadow } from "../constants/shadows";
import { colors } from "../constants/colors";

const HomeResults = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Результаты</Text>
      <ScrollView 
        style={styles.scroll}
        horizontal={true}
        showsHorizontalScrollIndicator={false}
        snapToEnd={false}
      > 
        <TouchableOpacity style={{...styles.card, ...shadow}}>
          <View style={styles.circle}>
            <Text style={styles.cardValue}>70%</Text>
          </View>
          <Text style={styles.cardTitle}>Прогресс</Text>
        </TouchableOpacity>
        <TouchableOpacity style={{...styles.card, ...shadow}}>
          <View style={styles.circle}>
            <Text style={styles.cardValue}>3</Text>
          </View>
          <Text style={styles.cardTitle}>Просрочено</Text>
        </TouchableOpacity>
        <TouchableOpacity style={{...styles.card, ...shadow}}>
          <View style={styles.circle}>
            <Text style={styles.cardValue}>5</Text>
          </View>
          <Text style={styles.cardTitle}>Выполнено</Text>
        </TouchableOpacity>
        <TouchableOpacity style={{...styles.card, ...shadow, marginRight: 20,}}>
          <View style={styles.circle}>
            <Text style={styles.cardValue}>2</Text>
          </View>
          <Text style={styles.cardTitle}>Осталось</Text>
        </TouchableOpacity>
      </ScrollView>
    </View>

  )
}

export default HomeResults;

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 30,
    lineHeight: 30,
    fontWeight: '600',
    marginBottom: 15,
    marginLeft: 20,
  },
  scroll: {
    paddingTop: 10,
    paddingBottom: 20,
  },
  card: {
    paddingVertical: 20,
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: '#fff',
    height: 120,
    width: 180,
    marginLeft: 20,
    borderRadius: 20,
  },
  circle: {
    width: 50,
    height: 50,
    borderRadius: 25,
    borderWidth: 1,
    borderColor: colors.BLUE,
    justifyContent: "center",
    alignItems: "center",
  },
  cardValue: {
    fontSize: 16,

  },
  cardTitle: {
    fontSize: 18,
  },
});
