import React from "react";
import { View, ScrollView, StyleSheet, TouchableWithoutFeedback, Image, Text } from "react-native";
import { BlurView } from "expo-blur";
import { colors } from "../constants/colors";
import { shadow } from "../constants/shadows";


const HomeSlider = ({navigation, slides}) => {
  return (
    <View style={styles.container}>
      <ScrollView 
        horizontal={true}
        showsHorizontalScrollIndicator={false}
        snapToEnd={false}
        style={styles.scroll}
      >
        {
          slides.map(({name, image, slideStyle, title}, index) => (
            <TouchableWithoutFeedback
              key={index}
            >

                  <View style={{...styles.slideContent, ...shadow, marginLeft: index === 0 ? 20 : 0,}}>
                    <Text style={styles.title}>{title}</Text>
                    <Image 
                      source={image}
                      style={styles.image}
                    />
                  </View>

            </TouchableWithoutFeedback>
          ))
        }
      </ScrollView>
    </View>

  );
};

export default HomeSlider;

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
  },
  scroll: {
    paddingTop: 10,
    paddingBottom: 20,
    marginTop: -40,
  },
  image :{
    marginTop: 20,
    width: 70,
    height: 70,
  },
  imageLayout: { 
    width: 200,
    height: 200,
    marginRight: 20,
    borderRadius: 20,
  },
  slideContent: {
    height: '100%', 
    alignItems: "center",
    width: 200,
    height: 200,
    marginRight: 20,
    borderRadius: 20,
    backgroundColor: '#fff'
  },
  headingContainer: {
    backgroundColor: 'rgba(41, 53, 89, 0.5)',
    borderRadius: 20,
    overflow: "hidden",
  },
  blurContainer: {
    height: 100,
    justifyContent: "center",
    alignItems: 'center',
  },
  title: {
    marginTop: 25,
    color: colors.ACCENT,
    fontSize: 20,
    lineHeight: 20,
    fontWeight: '500',
    textAlign: "center",
  },
})
